using System; using System.Collections.Generic; using System.Linq; using System.Threading.Tasks; using Microsoft.AspNetCore.Mvc; using WeatherChannel.Models;  namespace WeatherChannel.Controllers {     public class HomeController : Controller     {         public IActionResult Index()         {             return View();         }          public IActionResult GetMessages()         {             var allWeatherReports = WeatherReport.GetWeatherReport();             return View(allWeatherReports);         }          public IActionResult About()         {             ViewData["Message"] = "Your application description page.";              return View();         }          public IActionResult Contact()         {             ViewData["Message"] = "Your contact page.";              return View();         }          public IActionResult Error()         {             return View();         }          public IActionResult RequestReport()         {             return View();         }          [HttpPost]         public IActionResult RequestReport(WeatherReport newWeatherReport)         {             newWeatherReport.Request();             return RedirectToAction("Index");         }     } }
